import React, { Component } from 'react'
import ListHeading from './ListHeading'
import ListItemsTable from './ListItemsTable'
import ListTrash from './ListTrash'
import PropTypes from 'prop-types';

export class ListScreen extends Component {
    constructor(props) {
        super(props);
        this.state = {
          listName: this.props.todoList.name,
          listOwner: this.props.todoList.owner
        };
      }
    getListName() {
        if (this.props.todoList) {
            let name = this.props.todoList.name;
            return this.props.todoList.name;
        }
        else
            return "";
    }
    getListOwner() {
        if (this.props.todoList) {
            let owner = this.props.todoList.owner;
            return this.props.todoList.owner;
        }
    }
    updateListName =(e)=> {
        this.setState({listName: e.target.value});
        this.props.todoList.name = this.state.listName;
      }
    updateListOwner =(e)=> {
        this.setState({listOwner: e.target.value});
        this.props.todoList.owner = this.state.listOwner;
      }

    render() {
        return (
            <div id="todo_list">
                <ListHeading goHome={this.props.goHome} />
                <ListTrash />
                <div id="list_details_container">
                    <div id="list_details_name_container" className="text_toolbar">
                        <span id="list_name_prompt">Name:</span>
                        <input 
                            defaultValue={this.getListName()} 
                            type="text" 
                            id="list_name_textfield"
                            onChange={(() => {
                                this.setState({listOwner: e.target.value});
                                this.updateListName;
                            })()}
                                />
                    </div>
                    <div id="list_details_owner_container" className="text_toolbar">
                        <span id="list_owner_prompt">Owner:</span>
                        <input 
                            defaultValue={this.getListOwner()}
                            type="text" 
                            id="list_owner_textfield"
                            onChange={this.updateListOwner} />
                    </div>
                </div>
                <ListItemsTable todoList={this.props.todoList}
                                goItem={this.props.goItem}
                                addItem={this.props.addItem}
                                loadList={this.props.loadList.bind(this)}
                                sortByTask={this.props.sortByTask}
                                sortByDate={this.props.sortByDate}
                                sortByCompleted={this.props.sortByCompleted}/>         
            </div>
        )
    }
}

export default ListScreen
