import React, { Component } from 'react'

export class ListItemCard extends Component {
    render() {
        return (
            <div className='list_item_card'>
                <div className='list_item_card_description'>
                    {this.props.listItem.description}
                </div>
                <div className='list_item_card_assigned_to'>
                    Assigned To: <strong>{this.props.listItem.assigned_to}</strong>
                </div>
                <div className='list_item_card_due_date'>
                    {this.props.listItem.due_date}
                </div>

                <div className='list_item_card_completed'hidden>
                    {'Completed'}
                </div>
                <div className='list_item_card_not_completed'hidden>
                    {'Pending'}
                </div>
                <p>
                    {(() => {
                            let completedDiv = document.createElement('div');
                            if (this.props.listItem.completed) {
                                completedDiv.innerHTML += "Completed";
                                completedDiv.setAttribute('class', 'list_item_card_completed');
                            }
                            else {
                                completedDiv.innerHTML += "Pending";
                                completedDiv.setAttribute('class', 'list_item_card_not_completed');
                            }
                        }
                    )()}
                </p>
            </div>
        )
    }
}

export default ListItemCard
